INSERT INTO `wp_wc_admin_notes` VALUES (99,'woocommerce-WooPayments-Aug-2023-update-needed','update','en_US','Action required: Security update for WooPayments (WooCommerce Payments) plugin','<strong>Your shop requires an important security update for the WooPayments (WooCommerce Payments) extension</strong>. The fix for this vulnerability was released on July 31. Please update immediately.','{}','pending','woocommerce.com','2024-04-13 19:50:40',NULL,0,'plain','',0,0,'info');
